package com.lms.cloudpan.entity.dao;import com.baomidou.mybatisplus.annotation.FieldFill;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class Authority implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "aid", type = IdType.AUTO)    private Integer aid;    /**     * 父级id     */    private Integer parentId;    private String icon;    private String path;    private String component;    private Integer visible;    /**    * 启用    */    private Integer enable;    private Integer deleteFlag;    /**    * 权限名    */    private String name;    private String perms;    private String authType;    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 更新时间     */    @TableField(fill = FieldFill.INSERT_UPDATE)    private LocalDateTime updateTime;}