package com.lms.cloudpan.entity.dao;import com.baomidou.mybatisplus.annotation.FieldFill;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class Role implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "rid", type = IdType.AUTO)    private Integer rid;    /**    * 启用    */    private Integer enable;    /**    * 角色名    */    @TableField("role_name")    private String roleName;    /**    * 角色描述    */    private String description;    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 更新时间     */    @TableField(fill = FieldFill.INSERT_UPDATE)    private LocalDateTime updateTime;}