package com.lms.cloudpan.entity.dto;import io.swagger.annotations.ApiModelProperty;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class RoleDto implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 启用     */    @ApiModelProperty("启用")    private Integer enable=1;    /**     * 角色名     */    @ApiModelProperty("角色名")    @NotBlank(message = "角色名不能为空")    @NotNull(message = "角色名不能为空")    private String name;    /**     * 角色描述     */    @ApiModelProperty("角色描述")    private String description;    public Integer getEnabled() {        return enable==null?1:enable;    }    @Override    public String toString() {        return "角色名: "+name+" ,角色描述: "+description+" ,启用角色: "+((enable==1)?"是":"否");    }}