package com.lms.cloudpan.entity.vo;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import java.io.Serializable;import java.time.LocalDateTime;import java.util.List;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class AuthorityVo implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    private Integer aid;    private Integer parentId;    private List<AuthorityVo> children;    /**     * 启用     */    private Integer enable;    /**     * 权限名     */    private String name;    private String perms;    private String icon;    private String path;    private String component;    private Integer visible;    /**     * 创建时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime createTime;    /**     * 更新时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime updateTime;}