package com.lms.cloudpan.entity.vo;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import java.io.Serializable;import java.time.LocalDateTime;import java.util.Date;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class RoleVo implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    private Integer rid;    /**     * 启用     */    private Integer enable;    /**     * 角色名     */    private String roleName;    /**     * 角色描述     */    private String description;    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime createTime;}